<?php$packages = array(	'agenda',	'actualites',);foreach($packages as $package) {	$path = realpath('./'.$package);	$objects =  new RecursiveDirectoryIterator($path);	$iterator = new RecursiveIteratorIterator($objects);	$filtered = new RegexIterator($iterator, '/(?<!_package_listing\.txt)$/i', RecursiveRegexIterator::GET_MATCH);		$files = array();	foreach($filtered as $file)		$files[] = str_replace('\\','/',$iterator->getSubPathname());			file_put_contents($package.'/'.$package.'_package_listing.txt',implode("\r\n", $files));}?>