<?phpdefine('DOWNLOAD_PATH', './');if ( !isset( $argv ) || !isset( $argv[1] ) )	$package = !empty($_REQUEST['package']) ? $_REQUEST['package'] : '';else	$package = $argv[1];$package = preg_replace('/[^a-z0-9_-]/i', '', $package);$path = realpath(DOWNLOAD_PATH);	if(!file_exists($path.'/'.$package.'_package_listing.txt'))	exit('Package not found...');	$files = file($path.'/'.$package.'_package_listing.txt');$zip_file = tempnam("tmp", "zip");$zip = new ZipArchive();if(($zip->open($zip_file, ZipArchive::OVERWRITE)) !== true)	exit('Could not write temporary file...');foreach($files as $file) {	$file = trim($file);	if(file_exists($path.'/'.$file))		$zip->addFile($path.'/'.$file, $file);	else		error_log("Could NOT add file {$path}/{$file} to the RENO archive.");}$zip->close();// Stream the file to the clientheader("Content-Type: application/zip");header("Content-Length: " . filesize($zip_file));header("Content-Disposition: attachment; filename=\"{$package}.zip\"");readfile($zip_file);unlink($zip_file); 